import { Component } from "react";
// import logo from "./logo.svg";
import Navigation from "./navigation.jsx";
import ImageLinkForm from "./imageLinkForm.jsx";
import Clarifai from "clarifai";
import Rank from "./Rank.jsx";
import SignIn from "../signIn/signIn.jsx";
import SignUp from "./../signUp/signUp.jsx";
import Particles from "react-particles-js";
import ImageDisplay from "./imageDisplay.jsx";
import "tachyons";
console.log(document.clientX);

const app = new Clarifai.App({ apiKey: "e0c918f4d9e144a0b85687eedd9a4375" });

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { url: "", concepts: null, route: "signIn", signedIn: true };
  }
  onInputChange = (e) => {
    try {
      this.setState({ url: e.target.value, concepts: null });
    } catch {}
  };
  onSubmit = () => {
    let resp = null;
    app.models
      .predict(Clarifai.GENERAL_MODEL, this.state.url)
      .then(
        function (response) {
          resp = response.outputs[0].data.concepts;
        },
        function (err) {
          console.warn(err);
        }
      )
      .then(() => {
        console.log("hello");
        this.setState({ concepts: resp }, () => console.log(this.state));
      });
  };
  routeChange = (newRoute) => {
    this.setState({ route: newRoute });
  };
  onSignIn = () => {
    this.routeChange("home");
  };
  onSignUp = () => {
    this.routeChange("home");
  };
  onSignOut = () => {
    this.routeChange("home");
  };
  render() {
    return (
      <div className="App">
        <Particles
          className="background animatedBg"
          params={{
            particles: {
              number: {
                value: (window.innerHeight * window.innerWidth) / 10000,
              },
              size: {
                value: 3,
              },
            },
            interactivity: {
              events: {
                onclick: {
                  enable: true,
                  mode: "repulse",
                },
              },
            },
          }}
        />
        {this.state.signedIn ? (
          <nav className="flex">
            <Rank />
            <Navigation
              onSignOut={this.onSignOut}
              routeChange={this.routeChange}
              signedIn={this.state.signedIn}
            />
          </nav>
        ) : null}
        {this.state.route === "signIn" ? (
          <SignIn onSignIn={this.onSignIn} routeChange={this.routeChange} />
        ) : null}
        {this.state.route === "signUp" ? (
          <SignUp onSignUp={this.onSignIn} routeChange={this.routeChange} />
        ) : null}
        {this.state.route === "home" ? (
          <div>
            <ImageLinkForm
              onInputChange={this.onInputChange}
              onSubmit={this.onSubmit}
            />
            <ImageDisplay url={this.state.url} concepts={this.state.concepts} />
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
