import { Component } from "react";
// import logo from "./logo.svg";
import Navigation from "./components/navigation.jsx";
import ImageLinkForm from "./components/imageLinkForm.jsx";
import Clarifai from "clarifai";
import Rank from "./components/Rank.jsx";
import Particles from "react-particles-js";
import ImageDisplay from "./components/imageDisplay.jsx";
import Graph from "./components/graph.jsx";
import "tachyons";
console.log(document.clientX);

const app = new Clarifai.App({ apiKey: "e0c918f4d9e144a0b85687eedd9a4375" });

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { url: "", concepts: null };
  }
  onInputChange = (e) => {
    try {
      this.setState({ url: e.target.value, concepts: null });
    } catch {}
  };
  onSubmit = () => {
    let resp = null;
    app.models
      .predict(Clarifai.GENERAL_MODEL, this.state.url)
      .then(
        function (response) {
          resp = response.outputs[0].data.concepts;
        },
        function (err) {
          console.warn(err);
        }
      )
      .then(() => {
        console.log("hello");
        this.setState({ concepts: resp }, () => console.log(this.state));
      });
  };
  render() {
    return (
      <div className="App">
        <Particles
          className="background"
          params={{
            particles: {
              number: {
                value: (window.innerHeight * window.innerWidth) / 10000,
              },
              size: {
                value: 3,
              },
            },
            interactivity: {
              events: {
                onclick: {
                  enable: true,
                  mode: "repulse",
                },
              },
            },
          }}
        />
        <Navigation />
        <Rank />
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onSubmit={this.onSubmit}
        />
        <Graph
          itemsNumber={15}
          concepts={[
            {
              id: "ai_TJ9wFfK5",
              name: "portrait",
              value: 0.99354845,
              app_id: "main",
            },
            {
              id: "ai_SVshtN54",
              name: "one",
              value: 0.98571116,
              app_id: "main",
            },
            {
              id: "ai_dxSG2s86",
              name: "man",
              value: 0.9889341,
              app_id: "main",
            },
            {
              id: "ai_l8TKp2h5",
              name: "people",
              value: 0.9853919,
              app_id: "main",
            },
            {
              id: "ai_VPmHr5bm",
              name: "adult",
              value: 0.98204,
              app_id: "main",
            },
            {
              id: "ai_13NdwKqz",
              name: "festival",
              value: 0.9815703,
              app_id: "main",
            },
            {
              id: "ai_PzRPjgcp",
              name: "facial hair",
              value: 0.9788352,
              app_id: "main",
            },
            {
              id: "ai_86sS08Pw",
              name: "wear",
              value: 0.9622925,
              app_id: "main",
            },
            {
              id: "ai_S81BZrtF",
              name: "musician",
              value: 0.95367235,
              app_id: "main",
            },
            {
              id: "ai_GxSDhQ34",
              name: "facial expression",
              value: 0.9511193,
              app_id: "main",
            },
            {
              id: "ai_J5tkrb20",
              name: "eyewear",
              value: 0.9452471,
              app_id: "main",
            },
            {
              id: "ai_k76BrtPJ",
              name: "music",
              value: 0.9451725,
              app_id: "main",
            },
          ]}
        />
        <ImageDisplay url={this.state.url} concepts={this.state.concepts} />
      </div>
    );
  }
}

export default App;
