import { render } from "@testing-library/react";
import React from "react";
import Graph from "./graph.jsx";
import Slider from "./slider.jsx";
class ImageDisplay extends Component {
  constructor(props) {
    super(props);
    this.state = { value: 8 };
  }
  render() {
    return (
      <div className="imageDisplay mt2 flex flex-column">
        {props?.concepts?.length > 0 ? (
          // <div className="w-40 results bg-black-20 br3 pa2 ml2 shadow-5">
          // </div>
          <div>
            <Slider
              initialValue={8}
              onChange={(e) => (this.setState = { value: e.target.value })}
              value={this.state.value}
            />
            <Graph itemsNumber={8} concepts={props?.concepts} />
          </div>
        ) : null}
        {props?.url?.length > 0 ? (
          // eslint-disable-next-line jsx-a11y/img-redundant-alt
          <img
            src={props.url}
            alt="Invalid image"
            className="f3 dark-red b shadow-5 v-mid"
          />
        ) : null}
      </div>
    );
  }
}
export default ImageDisplay;
