import { Component } from "react";
// import logo from "./logo.svg";
import Navigation from "./navigation.jsx";
import ImageLinkForm from "./imageLinkForm.jsx";
import Clarifai from "clarifai";
import Rank from "./Rank.jsx";
import SignIn from "../signIn/signIn.jsx";
import SignUp from "./../signUp/signUp.jsx";
import Particles from "react-particles-js";
import ImageDisplay from "./imageDisplay.jsx";
import "tachyons";
console.log(document.clientX);

const particlesParams = {
  particles: {
    number: {
      value: (window.innerHeight * window.innerWidth) / 8000,
    },
    size: {
      value: 3,
    },
  },
  interactivity: {
    events: {
      onclick: {
        enable: true,
        mode: "repulse",
      },
    },
  },
};

const app = new Clarifai.App({ apiKey: "e0c918f4d9e144a0b85687eedd9a4375" });

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { url: "", concepts: null, route: "home", signedIn: false };
  }
  onInputChange = (e) => {
    try {
      this.setState({ url: e.target.value, concepts: null });
    } catch {}
  };
  onSubmit = () => {
    let resp = null;
    app.models
      .predict(Clarifai.GENERAL_MODEL, this.state.url)
      .then(
        function (response) {
          resp = response.outputs[0].data.concepts;
        },
        function (err) {
          console.warn(err);
        }
      )
      .then(() => {
        console.log("hello");
        this.setState({ concepts: resp }, () => console.log(this.state));
      });
  };
  routeChange = (newRoute) => {
    this.setState({ route: newRoute });
  };
  onSignIn = () => {
    this.routeChange("home");
    this.setState({ signedIn: true });
  };
  onSignUp = () => {
    this.routeChange("home");
  };
  onSignOut = () => {
    this.routeChange("home");
    this.setState({ signedIn: false });
  };
  render() {
    return (
      <div className="App">
        <Particles className="background animatedBg" params={particlesParams} />
        <nav className="flex ">
          {this.state.signedIn ? <Rank /> : null}
          <Navigation
            onSignOut={this.onSignOut}
            routeChange={this.routeChange}
            signedIn={this.state.signedIn}
          />
        </nav>

        {this.state.route === "signIn" ? (
          <SignIn onSignIn={this.onSignIn} routeChange={this.routeChange} />
        ) : null}
        {this.state.route === "signUp" ? (
          <SignUp onSignUp={this.onSignUp} routeChange={this.routeChange} />
        ) : null}
        {this.state.signedIn ? (
          <div>
            <ImageLinkForm
              onInputChange={this.onInputChange}
              onSubmit={this.onSubmit}
            />
            <ImageDisplay url={this.state.url} concepts={this.state.concepts} />
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
