import React from "react";
import {
  VictoryBar,
  VictoryChart,
  VictoryTheme,
  VictoryPolarAxis,
  LineSegment,
} from "victory";
import { g, Arc } from "victory-core";
const Graph = (props) => {
  const deltaMin = 0.1;
  const max = 1.0;
  let min = 1;
  const concepts = props?.concepts.slice(0, props?.itemsNumber);
  concepts.map((x) => (x.value < min ? (min = x.value) : null));
  min -= deltaMin;
  const shiftedData = [];
  console.log(min, max);
  concepts.map((x) => shiftedData.push({ name: x.name, value: x.value - min }));
  return (
    <VictoryChart
      polar
      invertAxis={false}
      startAngle={60}
      endAngle={-60}
      axisAngle={45}
      padding={{ top: 100, bottom: 0 }}
      domain={{ y: [0, max - min] }}
      height={500}
      animate={{
        duration: 1000,
        onLoad: { duration: 1000, easing: "cubicOut" },
      }}
      innerRadius={20}
    >
      {shiftedData
        .reduce((acc, val) => {
          acc.push(val.name);
          return acc;
        }, [])
        .map((d, i) => {
          return (
            <VictoryPolarAxis
              dependentAxis
              key={i}
              label={d}
              labelPlacement="vertical"
              style={{
                axis: {
                  stroke: "#abcdefff",
                  strokeWidth: 1,
                },
                tickLabels: { fill: "none" },
              }}
              axisValue={d}
            />
          );
        })}
      <VictoryBar
        style={{ data: { fill: "tomato", width: 5 } }}
        data={shiftedData}
        x="name"
        y="value"
      />
    </VictoryChart>
  );
};
export default Graph;
