import { Component } from "react";
// import logo from "./logo.svg";
import Navigation from "./navigation.jsx";
import ImageLinkForm from "./imageLinkForm.jsx";
import Clarifai from "clarifai";
import Rank from "./Rank.jsx";
import SignIn from "./../sign-in/signIn.jsx";
import Particles from "react-particles-js";
import ImageDisplay from "./imageDisplay.jsx";
import "tachyons";
console.log(document.clientX);

const app = new Clarifai.App({ apiKey: "e0c918f4d9e144a0b85687eedd9a4375" });

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { url: "", concepts: null, route: "signIn" };
  }
  onInputChange = (e) => {
    try {
      this.setState({ url: e.target.value, concepts: null });
    } catch {}
  };
  onSubmit = () => {
    let resp = null;
    app.models
      .predict(Clarifai.GENERAL_MODEL, this.state.url)
      .then(
        function (response) {
          resp = response.outputs[0].data.concepts;
        },
        function (err) {
          console.warn(err);
        }
      )
      .then(() => {
        console.log("hello");
        this.setState({ concepts: resp }, () => console.log(this.state));
      });
  };
  routeChange = (newRoute) => {
    this.setState({ route: newRoute });
  };
  onSignIn = () => {};
  render() {
    return (
      <div className="App">
        <Particles
          className="background animatedBg"
          params={{
            particles: {
              number: {
                value: (window.innerHeight * window.innerWidth) / 10000,
              },
              size: {
                value: 3,
              },
            },
            interactivity: {
              events: {
                onclick: {
                  enable: true,
                  mode: "repulse",
                },
              },
            },
          }}
        />
        <nav className="flex">
          <Rank />
          <Navigation />
        </nav>
        {this.state.route === "signIn" ? <SignIn /> : null}
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onSubmit={this.onSubmit}
        />

        <ImageDisplay url={this.state.url} concepts={this.state.concepts} />
      </div>
    );
  }
}

export default App;
