import { Component } from "react";
import logo from "./logo.svg";
import Navigation from "./components/navigation.jsx";
import ImageLinkForm from "./components/imageLinkForm.jsx";
import Clarifai from "clarifai";
import Rank from "./components/Rank.jsx";
import Particles from "react-particles-js";
import ImageDisplay from "./components/imageDisplay.jsx";

import "tachyons";
console.log(document.clientX);

const app = new Clarifai.App({ apiKey: "e0c918f4d9e144a0b85687eedd9a4375" });

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { url: "", concepts: null };
  }
  onInputChange = (e) => {
    try {
      this.setState({ url: e.target.value });
    } catch {}
  };
  onSubmit = () => {
    app.models.predict(Clarifai.GENERAL_MODEL, this.state.url).then(
      function (response) {
        const c = response.outputs[0].data.concepts;
        this.setState({ concepts: c }, console.log("States : ", this.state));
      },
      function (err) {
        console.warn(err);
      }
    );
  };
  render() {
    return (
      <div className="App">
        <Particles
          className="background"
          params={{
            particles: {
              number: {
                value: (window.innerHeight * window.innerWidth) / 10000,
              },
              size: {
                value: 3,
              },
            },
            interactivity: {
              events: {
                onclick: {
                  enable: true,
                  mode: "repulse",
                },
              },
            },
          }}
        />
        <Navigation />
        <Rank />
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onSubmit={this.onSubmit}
        />
        <ImageDisplay url={this.state.url} concepts={this.state.concepts} />
      </div>
    );
  }
}

export default App;
