import React from "react";
import {
  VictoryBar,
  VictoryChart,
  VictoryTheme,
  VictoryPolarAxis,
  LineSegment,
} from "victory";
const Graph = (props) => {
  const deltaMin = 0.05;
  const max = 1.02;
  let min = 1;
  const concepts = props?.concepts.slice(0, props?.itemsNumber);
  concepts.map((x) => (x.value < min ? (min = x.value) : null));
  min -= deltaMin;
  const shiftedData = [];
  concepts.map((x) => shiftedData.push({ name: x.name, value: x.value - min }));
  return (
    <VictoryChart
      polar
      theme={VictoryTheme.material}
      startAngle={150}
      endAngle={30}
      domain={{ y: [0, max - min] }}
      animate={{ duration: 500, onLoad: { duration: 1000 } }}
      innerRadius={30}
      tickCount={1}
    >
      {shiftedData
        .reduce((acc, val) => {
          acc.push(val.name);
          return acc;
        }, [])
        .map((d, i) => {
          return (
            <VictoryPolarAxis
              dependentAxis
              key={i}
              label={d}
              labelPlacement="parallel"
              style={{
                axis: {
                  fill: "transparent",
                  strokeWidth: 1,
                },
                tickLabels: { fill: "none" },
                ticks: {
                  fill: "transparent",
                  strokeWidth: 10,
                  stroke: "transparent",
                },
              }}
              axisValue={d}
            />
          );
        })}
      <svg style={{ height: 0 }}>
        <defs>
          <RadialGradient id="myGradient">
            <stop offset="0%" stopColor="red" />
            <stop offset="25%" stopColor="orange" />
            <stop offset="50%" stopColor="gold" />
            <stop offset="75%" stopColor="yellow" />
            <stop offset="100%" stopColor="green" />
          </RadialGradient>
        </defs>
      </svg>
      <VictoryBar
        style={{ data: { fill: "url(#myGradient)", width: 5 } }}
        data={shiftedData}
        x="name"
        y="value"
      />
    </VictoryChart>
  );
};
export default Graph;
