import { render } from "@testing-library/react";
import React from "react";
import { Component } from "react";
import Graph from "./graph.jsx";
import Slider from "./slider.jsx";
class ImageDisplay extends Component {
  constructor(props) {
    super(props);
    this.state = { value: 8 };
  }
  getImageRatio(url) {
    var img = new Image();
    img.src = url;
    return img.width / img.height;
  }

  render() {
    return (
      <div className="imageDisplay ml1 mt1 flex flex-column justify-center">
        {this.props?.concepts?.length > 0 ? (
          // <div className="w-40 results bg-black-20 br3 pa2 ml2 shadow-5">
          // </div>
          <Slider
            className="ma2"
            initialValue={8}
            onChange={(e) => {
              this.setState({ value: e.target.value });
            }}
            value={this.state.value}
          />
        ) : null}
        <div
          className={
            "flex justify-center align-center" +
              this.getImageRatio(this.props.url) >
            window.innerWidth / windows.innerHeight
          }
        >
          {this.props?.url?.length > 0 ? (
            // eslint-disable-next-line jsx-a11y/img-redundant-alt
            <img
              src={this.props.url}
              alt="Invalid image"
              className="f3 dark-red b v-mid"
            />
          ) : null}
          {this.props?.concepts?.length > 0 ? (
            // <div className="w-40 results bg-black-20 br3 pa2 ml2 shadow-5">
            // </div>
            <div className="resultContainer w-50 bg-black-30 br4 shadow-5 ma2">
              <Graph
                className="flex justify-center align-center"
                itemsNumber={this.state.value}
                concepts={this.props?.concepts}
              />
            </div>
          ) : null}
        </div>
      </div>
    );
  }
}
export default ImageDisplay;
